contentCss:
body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
  line-height: 1.4;
  margin: 1rem;
}

table {
  border-collapse: collapse;
}

table th,
table td {
  border: 1px solid #ccc;
  padding: .4rem;
}

figure {
  display: table;
  margin: 1rem auto;
}

figure figcaption {
  color: #999;
  display: block;
  margin-top: .25rem;
  text-align: center;
}

hr {
  border-color: #ccc;
  border-style: solid;
  border-width: 1px 0 0 0;
}

code {
  background-color: #e8e8e8;
  border-radius: 2px;
  padding: .1rem .2rem;
}

.mce-content-body:not([dir=rtl]) {
  blockquote {
    border-left: 2px solid #ccc;
    margin-left: 1.5rem;
    padding-left: 1rem;
  }
}

// RTL
.mce-content-body[dir=rtl] {
  blockquote {
    border-right: 2px solid #ccc;
    margin-right: 1.5rem;
    padding-right: 1rem;
  }
}

initConfig:
tinymce.init({
  plugins: [
    "a11ychecker advcode casechange formatpainter",
    "linkchecker autolink lists checklist",
    "media mediaembed pageembed permanentpen",
    "powerpaste table advtable tinymcespellchecker"
  ],
  toolbar: "formatselect | fontselect | bold italic strikethrough forecolor backcolor formatpainter | alignleft aligncenter alignright alignjustify | numlist bullist outdent indent | link insertfile image | removeformat | code | addcomment | checklist | casechange",
  height: 360
});

lessVariables:
@background-color: #fff;
@toolbar-background-color: rgba(255, 255, 255, 1);
@menubar-background-color: rgba(255, 255, 255, 1);
@border-color: rgba(217, 217, 217, 1);
@color-black: rgba(0, 0, 0, 0.85);
@color-white: #fff;
@color-tint: rgba(8, 151, 156, 1);
@color-error: rgba(245, 34, 45, 1);
@color-success: rgba(82, 196, 26, 1);
@base-value: 14px;
@control-border-radius: 2px;
@panel-border-radius: 2px;
@tinymce-border-radius: 2px;
@font-stack: -apple-system,BlinkMacSystemFont,Segoe UI,PingFang SC,Hiragino Sans GB,Microsoft YaHei,Helvetica Neue,Helvetica,Arial,sans-serif,Apple Color Emoji,Segoe UI Emoji,Segoe UI Symbol;
@font-weight-normal: normal;
@font-weight-bold: 500;
@toolbar-button-icon-color: contrast(@background-color, @color-white, @color-black);
@toolbar-button-text-color: contrast(@background-color, @color-white, @color-black);
@toolbar-button-hover-background-color: rgba(8, 151, 156, 1);
@toolbar-button-hover-icon-color: rgba(255, 255, 255, 1);
@toolbar-button-hover-text-color: rgba(255, 255, 255, 1);
@toolbar-button-focus-background-color: rgba(8, 151, 156, 1);
@toolbar-button-focus-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-focus-text-color: @toolbar-button-hover-icon-color;
@toolbar-button-active-background-color: @toolbar-button-hover-background-color;
@toolbar-button-enabled-background-color: @toolbar-button-hover-background-color;
@toolbar-button-active-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-active-text-color: @toolbar-button-hover-icon-color;
@toolbar-button-enabled-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-enabled-text-color: @toolbar-button-hover-icon-color;
